var express = require('express');
var mysql = require('mysql');
var app = express();

var connection = mysql.createConnection({

    host: 'localhost',
    user: 'root',
    password: '',
    database: 'teste'
});

connection.connect(function(error) {
    if (error) {
        console.log('Error');
    }
    else {
        console.log('Connected');
    }
});

app.get('/', function(req, res) {
    connection.query("SELECT * FROM aluno", function(error, rows, fields) {
        connection.end();
        if (error) {
            connection.release();
            console.log('Error in the query');
        }
        else {
            console.log('Successful query');
            res.send(rows);
        }
    });
});

function InsertAluno()
{
    app.get('/', function(req, res) {
        connection.query("INSERT INTO aluno VALUES()", function(error, rows, fields) {
            connection.end();
            if (error) {
                connection.release();
                console.log('Error in the query');
            }
            else {
                console.log('Inserido com sucesso');
            }
        });
    });
}

app.listen(3000);